package Conditions;

public class Loops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Display a text 5 times.");
		for (int i=0; i<5;i++ )
		{
			System.out.println("Display");
		}
		System.out.println("Print number from 1 to 5.");
		for (int i=1;i<=5;i++)
		{
			System.out.print(i+" ");
		}
		System.out.println("Display sum of n natural number");
		int n =5;
		for (int i=1; i<n; i++)
		{
			System.out.println("Display");
		}

	}

}
